import tkinter as tkimport mathroot = tk.Tk()root.geometry("600x900")root.title("Python Hypercalculator")fieldDS = ""fieldEV = ""listDS = []listEV = []b = 5r = 1s = 1field=tk.Text(root,height=2,width=21,font=("Arial", 20))field.grid(row=1,column=1,columnspan=4)def addToField(sth, ds, ev, *alias):    global fieldDS    global fieldEV    if ds:        fieldDS += str(sth)        field.delete("1.0","end")        field.insert("1.0", fieldDS)        listDS.append(str(sth))    if ev:        fieldEV += str(sth)        listEV.append(str(sth))def calculate():    global fieldDS    global fieldEV    result = str(eval(fieldEV))    clear()    field.insert("1.0",result)    fieldDS += result    fieldEV += result    for char in result:        listDS.append(char)        listEV.append(char)def clear():    global fieldDS    global fieldEV    fieldDS = ""    fieldEV = ""    listDS.clear()    listEV.clear()    field.delete("1.0", "end")def backspace():    global fieldDS    global fieldEV    fieldDS = fieldDS[0:len(fieldDS) - len(listDS[-1])]    fieldEV = fieldEV[0:len(fieldEV) - len(listEV[-1])]    listDS.pop()    listEV.pop()    field.delete("1.0", "end")    field.insert("1.0", fieldDS)def factorial():    global fieldDS    global fieldEV    i = -1    c = 0    while i > -(len(listEV)) and listEV[i].isdigit():        c = i        i -= 1    x = fieldEV[c:]    n = math.factorial(int(x))    fieldEV = fieldEV[:-len(x)]    addToField(str(n), False, True)    addToField("!", True, False)btn_7 = tk.Button(root,text="7",command=lambda: addToField(7, True, True),width=5,font=("Times New Roman", 20))btn_7.grid(row=b,column=1)btn_8 = tk.Button(root,text="8",command=lambda: addToField(8, True, True),width=5,font=("Times New Roman", 20))btn_8.grid(row=b,column=2)btn_9 = tk.Button(root,text="9",command=lambda: addToField(9, True, True),width=5,font=("Times New Roman", 20))btn_9.grid(row=b,column=3)btn_4 = tk.Button(root,text="4",command=lambda: addToField(4, True, True),width=5,font=("Times New Roman", 20))btn_4.grid(row=b+1,column=1)btn_5 = tk.Button(root,text="5",command=lambda: addToField(5, True, True),width=5,font=("Times New Roman", 20))btn_5.grid(row=b+1,column=2)btn_6 = tk.Button(root,text="6",command=lambda: addToField(6, True, True),width=5,font=("Times New Roman", 20))btn_6.grid(row=b+1,column=3)btn_1 = tk.Button(root,text="1",command=lambda: addToField(1, True, True),width=5,font=("Times New Roman", 20))btn_1.grid(row=b+2,column=1)btn_2 = tk.Button(root,text="2",command=lambda: addToField(2, True, True),width=5,font=("Times New Roman", 20))btn_2.grid(row=b+2,column=2)btn_3 = tk.Button(root,text="3",command=lambda: addToField(3, True, True),width=5,font=("Times New Roman", 20))btn_3.grid(row=b+2,column=3)btn_0 = tk.Button(root,text="0",command=lambda: addToField(0, True, True),width=5,font=("Times New Roman", 20))btn_0.grid(row=b+3,column=1)btn_lb = tk.Button(root,text="(",command=lambda: addToField("(", True, True),width=5,font=("Times New Roman", 20))btn_lb.grid(row=b+1,column=4)btn_rb = tk.Button(root,text=")",command=lambda: addToField(")", True, True),width=5,font=("Times New Roman", 20))btn_rb.grid(row=b+1,column=5)btn_C = tk.Button(root,text="C",command=lambda: clear(),width=5,font=("Times New Roman", 20))btn_C.grid(row=b,column=4)btn_BS = tk.Button(root,text="BS",command=lambda: backspace(),width=5,font=("Times New Roman", 20))btn_BS.grid(row=b,column=5)btn_equals = tk.Button(root,text="=",command=lambda: calculate(),width=5,font=("Times New Roman", 20))btn_equals.grid(row=b+7,column=5)btn_add = tk.Button(root,text="+",command=lambda: addToField("+", True, True),width=5,font=("Times New Roman", 20))btn_add.grid(row=b+3,column=4)btn_subtract = tk.Button(root,text="-",command=lambda: addToField("-", True, True),width=5,font=("Times New Roman", 20))btn_subtract.grid(row=b+3,column=5)btn_multiply = tk.Button(root,text="x",command=lambda: addToField("*", True, True),width=5,font=("Times New Roman", 20))btn_multiply.grid(row=b+2,column=4)btn_divide = tk.Button(root,text="/",command=lambda: addToField("/", True, True),width=5,font=("Times New Roman", 20))btn_divide.grid(row=b+2,column=5)btn_factorial = tk.Button(root,text="!",command=lambda: factorial(),width=5,font=("Times New Roman", 20))btn_factorial.grid(row=b+4,column=5)root.mainloop()